const webSocketSetup = async ({
  text,
  currencyPair,
  callBack
}) => {




  let ws = new WebSocket('wss://ws.bitstamp.net/');

  if (text === 'subscribe') {
    console.log('closed');

    ws.onopen = () => {
      //Subscribe to the channel
      ws.send(JSON.stringify({ event: `bts:${text}`, data: { channel: `order_book_${currencyPair}` } }))


    }


    ws.onmessage = (msg) => {
      console.log(JSON.parse(msg.data), '=============>');

      // callBack && callBack(JSON.parse(msg.data));
    }
  } else {
    ws.close()
  }






  ws.onerror = error => {
    console.log(`WebSocket error: ${error}`)
  }

}

export default webSocketSetup;
